package baseball;public class PlayResult {    private int strike;    private int ball;    private PlayResult() {}    public static PlayResult init() {        return new PlayResult();    }    public boolean isNotGameEnd() {        return this.strike != 3;    }    public void note(PlayJudge judgement) {        if(judgement.isBall()) {            this.ball++;        }        if(judgement.isStrike()) {            this.strike++;        }    }    public void report() {        if(isNoBallAndNoStrike()) {            System.out.print("낫싱\n");        }        if(hasBallAndNoStrike()) {            System.out.print(this.ball + "볼\n");        }        if(hasStrikeAndNoBall()) {            System.out.print(this.strike + "스트라이크\n");        }        if(hasBallAndStrike()) {            System.out.print(this.ball + "볼 " + this.strike + "스트라이크\n");        }    }    private boolean hasBallAndStrike() {        return this.ball > 0 && this.strike > 0;    }    private boolean hasStrikeAndNoBall() {        return this.ball == 0 && this.strike > 0;    }    private boolean hasBallAndNoStrike() {        return this.ball > 0 && this.strike == 0;    }    private boolean isNoBallAndNoStrike() {        return this.ball == 0 && this.strike == 0;    }}